function not1(msg) {
    notif({
        msg: "<b>Success:</b> " + msg,
        type: "success"
    });
}

function not2(msg) {
    notif({
        msg: "<b>Oops!</b> " + msg,
        type: "error",
        position: "center"
    });
}

function not3(msg) {
    notif({
        type: "warning",
        msg: "<b>Warning:</b> " + msg,
        position: "left"
    });
}

function not4(msg) {
    notif({
        type: "info",
        msg: "<b>Info: </b>" + msg,
        width: "all",
        height: 100,
        position: "center"
    });
}

function not5(msg) {
    notif({
        type: "error",
        msg: "<b>Error: </b>" + msg,
        position: "center",
        width: 500,
        height: 60,
        autohide: false
    });
}

function not6(msg) {
    notif({
        type: "warning",
        msg: msg,
        position: "center",
        opacity: 0.8
    });
}
